import logo from './logo.svg';
import './App.css';
import QuestionForm from './components/form';
import QuestionCard from './components/question';
import 'bootstrap/dist/css/bootstrap.min.css';

let questions = [
  {
    id: 1,
    question: 'Can you explain our Q3 goals?',
    isAnswered: false,
    isAnonymous: false,
    voteCount: 1,
  },
  {
    id: 2,
    question: 'We would like new food in the cafeteria',
    isAnswered: false,
    isAnonymous: false,
    voteCount: 1,
  },
  {
    id: 3,
    question: 'When is our Halloween party?',
    isAnswered: false,
    isAnonymous: false,
    voteCount: 1,
  },
  {
    id: 4,
    question: 'Are we ethically going in the right direction?',
    isAnswered: false,
    isAnonymous: true,
    voteCount: 1,
  }
]

const handleAddQuestion = (question) => {
  questions.push(question);
}

function App() {
  return (
    <div className="App">
      <h1>Cricket Poll</h1>
      <h3>Add a Question</h3>
      <QuestionForm 
        handleAddQuestion={handleAddQuestion}
      />
      <h3>Vote on Submitted Questions</h3>
      {questions.map(element => 
        <QuestionCard 
          question={element.question} 
        />)}
    </div>
  );
}

export default App;
