import logo from './logo.svg';
import './App.css';
import QuestionForm from './components/form';
import QuestionCard from './components/question';
import 'bootstrap/dist/css/bootstrap.min.css';

let questions = [
  {
    id: 1,
    question: 'Can you explain our Q3 goals?',
    isAnswered: false,
    isAnonymous: false,
    voteCount: 1,
  },
  {
    id: 2,
    question: 'We would like new food in the cafeteria',
    isAnswered: false,
    isAnonymous: false,
    voteCount: 1,
  },
  {
    id: 3,
    question: 'When is our Halloween party?',
    isAnswered: false,
    isAnonymous: false,
    voteCount: 1,
  },
  {
    id: 4,
    question: 'Are we ethically going in the right direction?',
    isAnswered: false,
    isAnonymous: true,
    voteCount: 1,
  }
]

const handleAddQuestion = (question) => {
  questions.push(question);
}

function App() {
  return (
    <div className="App">
      <QuestionForm 
        handleAddQuestion={handleAddQuestion}
      />
      {questions.map(item => <QuestionCard question={item.question} />)}
    </div>
  );
}

export default App;
