import './App.css';
import QuestionForm from './components/form';
import QuestionCard from './components/question';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';

let defaultQuestions = [
  {
    id: 1,
    question: 'Can you explain our Q3 goals?',
    isAnswered: false,
    isAnonymous: false,
    voteCount: 1,
  },
  {
    id: 2,
    question: 'We would like new food in the cafeteria',
    isAnswered: false,
    isAnonymous: false,
    voteCount: 1,
  },
  {
    id: 3,
    question: 'When is our Halloween party?',
    isAnswered: false,
    isAnonymous: false,
    voteCount: 1,
  },
  {
    id: 4,
    question: 'Are we ethically going in the right direction?',
    isAnswered: false,
    isAnonymous: true,
    voteCount: 1,
  }
]

function App() {

  const [questions, setQuestions] = useState(defaultQuestions);

  const handleAddQuestion = (value) => {
    setQuestions([...questions, {id: (questions.length + 1), question: value, isAnswered: false, isAnonymous: false, voteCount: 1 }]);
  }

  const sortedQuestions = questions.sort((a, b) => b.voteCount - a.voteCount);

  const handleUpVote = (value) => {
    const foundQuestion = questions.find(value === questions.id);

    foundQuestion.voteCount++;
  }

  const handleDownVote = (value) => {
    if(value.voteCount > 0) {
      value.voteCount--;
    }
  }

  return (
    <div className="App">
      <h1>Poll</h1>
      <h3>Add a Question</h3>
      <QuestionForm 
        handleAddQuestion={handleAddQuestion}
      />
      <h3>Vote on Submitted Questions</h3>
      {sortedQuestions.map(element => 
        <QuestionCard
          key={element.id} 
          question={element.question}
          votes={element.voteCount}
          handleUpVote={handleUpVote}
          handleDownVote={handleDownVote} 
        />)}
    </div>
  );
}

export default App;
